import {useState} from "react";

function Card({id,name,info,image,price,removeTour}){
    const [readmore,setReadmore]=useState(false);
    const description=readmore?info:`${info.substring(0,200)}....`;
    function readmoreHandler(){
        setReadmore(!readmore);
    }
    return (
        //Let's create a card using the fetched details
        <div className="card w-96 h-full m-1 gap-4 p-2 flex flex-col rounded-lg items-center shadow-lg shadow-black">
            <img src={image} className="w-90 aspect-square object-cover"></img>
            <div className="datails m">
                <div className="price-name">
                    <h1 className="tour-price">{price}</h1>
                    <h1 className="tour-name">{name}</h1>
                </div>
                <div className="tour-info">
                    {description}
                    <span className="read-more" onClick={readmoreHandler}>
                        {readmore?`Shoe Less`:`Show More`};
                    </span>
                </div>
            </div>
            <button className="btn" onClick={()=>removeTour(id)}>Not Interested</button>
        </div>
    );
}
export default Card;
